import tkinter as tk
import random
import time

class PersonalityTest:
    def __init__(self):
        self.name = ""
        self.age = 0
        self.answers = {}

    def ask_basic_info(self):
        self.name = input("\nwhat is your name? \n\n>>> ").strip()
        while True:
            try:
                self.age = int(input("\nhow old are you? \n\n>>> ").strip())
                break
            except ValueError:
                print("please enter a valid number.")
    def ask_mood_questions(self):
        self.answers["time"] = input("\ndo you ever feel like time is slipping away? (yes/no) \n\n>>> ").strip().lower()
        self.answers["change_past"] = input("\nif you could change one moment from your past, would you? (yes/no)\n\n>>> ").strip().lower()
        self.answers["memories"] = input("\ndo you believe memories shape who we are, or do we shape our memories? (first/second)\n\n>>> ").strip().lower()
        self.answers["peace"] = input("\nwhen was the last time you felt truly at peace? \n\n>>> ").strip()
        self.answers["dream"] = input("\ndo you ever dream of a place you've never been? (yes/no) \n\n>>> ").strip().lower()
        
        if self.answers["dream"] in ["yes", "y", "oui"]:
            self.answers["dream_place"] = input("\nwhat is this place? \n\n>>> ").strip()
        else:
            self.answers["dream_place"] = None  

        self.answers["regret"] = input("\ndo you regret something deeply? (yes/no) \n\n>>> ").strip().lower()
        self.answers["fear"] = input("\ndo you fear being forgotten? (yes/no) \n\n>>> ").strip().lower()

    def print_results(self):
        filename = f"dear_{self.name.lower().replace(' ', '_')}.txt"

        with open(filename, "w", encoding="utf-8") as file:
            file.write(f"hi {self.name}.\n\n")

            # question sur le temps
            if self.answers["time"] in ["yes", "y", "oui"]:
                file.write(
                    "time slips through your fingers like sand. you feel it passing, unstoppable, "
                    "a quiet reminder that every second lost can never be reclaimed.\n\n"
                )
            else:
                file.write(
                    "time moves forward, steady and unyielding. you don’t dwell on its passage—"
                    "instead, you let it flow around you like a river that never truly stops.\n\n"
                )

            # question sur le passé
            if self.answers["change_past"] in ["yes", "y", "oui"]:
                file.write(
                    "you often find yourself lost in thoughts of what could have been. "
                    "the past lingers, whispering what-ifs in your ears, but the present does not wait.\n\n"
                )
            else:
                file.write(
                    "you accept the past as it is—an unchangeable force, neither friend nor enemy. "
                    "mistakes, triumphs, regrets… all form the foundation of who you are today.\n\n"
                )

            # question sur la mémoire
            if self.answers["memories"] in ["first", "1", "memories shape us"]:
                file.write(
                    f"memories carve deep paths within you, defining the roads you take since {self.age}. "
                    "each experience shapes your being, leaving imprints like footsteps in the sand.\n\n"
                )
            else:
                file.write(
                    "you are the sculptor of your memories. they shift, reshape, change over time—"
                    "not fixed moments, but reflections of who you choose to become.\n\n"
                )

            # question sur la paix
            file.write(
                f"that feeling of peace... when was it? you remember {self.answers['peace']}, "
                "a fleeting moment etched into your mind. do you wish you could return to it, or is it still waiting ahead?\n\n"
            )

            # question sur les regrets
            if self.answers["regret"] in ["yes", "y", "oui"]:
                file.write(
                    "some regrets never fade. they linger in the corners of your mind, resurfacing when you least expect them. "
                    "perhaps you wish you could undo them, but the past is unrelenting.\n\n"
                )
            else:
                file.write(
                    "regret is a chain some people carry, but you have let yours go. "
                    "you choose to move forward, accepting the choices that have led you here.\n\n"
                )

            # question sur la peur d'être oublié
            if self.answers["fear"] in ["yes", "y", "oui"]:
                file.write(
                    "you fear being forgotten, as if your existence depends on the memories of others. "
                    "perhaps this is why you hold onto moments so tightly, afraid they will slip away.\n\n"
                )
            else:
                file.write(
                    "whether people remember you or not does not change who you are. "
                    "you exist, here and now, and that is enough.\n\n"
                )

            # question sur les rêves (divisée en deux parties)
            if self.answers["dream"] in ["yes", "y", "oui"] and self.answers["dream_place"]:
                file.write(
                    f"and the dreams… you often see yourself in {self.answers['dream_place']}, "
                    "a place that calls to you despite never having been there. is it a memory from another life, "
                    "or a destination waiting beyond the horizon?\n\n"
                )
            elif self.answers["dream"] in ["no", "n", "non"]:
                file.write(
                    "your nights are quiet, void of strange landscapes or forgotten places. "
                    "perhaps your mind is at peace, or perhaps it is simply waiting for a reason to dream.\n\n"
                )

            file.write("who are we, if not the sum of these thoughts?\n")

        print(f"\nthanks for your honesty, {self.name}.\n")
    
    def run(self):
        self.ask_basic_info()
        self.ask_mood_questions()
        self.print_results()  

class GameApp:
    def __init__(self, root):
        self.personality = personality
        self.root = root
        self.root.title("game")
        self.root.geometry("600x400")
        self.root.configure(bg="black")

        self.label = tk.Label(
            root,
            text="you have been here, in space, for longer than you can remember.\n\n"
            "you don't remember your name, nor how you arrived here.\n"
            "the vast emptiness surrounds you, an endless void stretching in every direction.\n\n"
            "were you sent here for a purpose, or is this a cruel trick of fate?\n"
            "the only thing that remains is the choice before you.\n\n"
            "the stars whisper secrets lost to time.",
            fg="white",
            bg="black",
            font=("Arial", 18),
            wraplength=550,
            justify="center",
        )
        self.label.pack(expand=True)

        self.root.after(15000, self.start_game)

    def start_game(self):
        self.label.config(
            text="you’re at the window, watching earth drift farther away.\nwhat do you do?"
        )

        self.button_frame = tk.Frame(self.root, bg="black")
        self.button_frame.pack()

        self.option1 = tk.Button(
            self.button_frame,
            text="call my family.",
            command=self.choice_0,
            fg="black",
            bg="black",
            font=("Arial", 16),
            borderwidth=0,
        )
        self.option1.pack(side="left", padx=10)

        self.option2 = tk.Button(
            self.button_frame,
            text="recall my memories.",
            command=self.choice_1,
            fg="black",
            bg="black",
            font=("Arial", 16),
            borderwidth=0,
        )
        self.option2.pack(side="right", padx=10)

    def choice_0(self):
        self.label.config(
            text="you try to call your family, but the connection is weak in deep space."
        )
        self.clear_buttons()

    def choice_1(self):
        self.advance_story(0)

    def advance_story(self, step):
        choices = [
            (
                "a fragment of your past emerges. you see a lighthouse in the distance. what do you do?",
                "walk towards it.",
                "ignore it and keep walking.",
            ),
            (
                "the lighthouse door is ajar. do you enter?",
                "step inside.",
                "circle around it first.",
            ),
            (
                "inside, the air is heavy with dust. two doors stand before you. which do you take?",
                "go through the left door.",
                "go through the right door.",
            ),
            (
                "the right door creaks open. a child stands in the dim light, facing away. do you call out?",
                "say 'hello'.",
                "wait and observe.",
            ),
            (
                "the child turns around. it's... you, from years ago. he stares, eyes wide. what do you say?",
                "ask him if he's okay.",
                "ask him why he's here.",
            ),
            (
                "he hesitates, then whispers: 'are you happy?'. how do you respond?",
                "yes, i am.",
                "no, i'm not.",
            ),
            (
                "tears form in his eyes. he nods, then says: 'turn around.'",
                "turn around slowly.",
                "refuse and stay still.",
            ),
            (
                "a house now stands where there was only mist. do you approach?",
                "walk toward it.",
                "wait and observe.",
            ),
            (
                "as you step closer, the house moves further away. what do you do?",
                "run towards it.",
                "stand still and watch.",
            ),
            (
                "the house remains distant, fading. a new path appears. do you follow it?",
                "follow the path.",
                "turn back the way you came.",
            ),
            (
                "a soft hum vibrates in the air, mechanical yet familiar. do you listen?",
                "close your eyes and focus on the sound.",
                "ignore it and move forward.",
            ),
            (
                "a panel in the floor glows faintly. do you investigate?",
                "step closer and examine it.",
                "leave it alone.",
            ),
            (
                "the stars beyond the window seem to shift, forming patterns. do you try to understand?",
                "trace them with your fingers.",
                "look away.",
            ),
            (
                f"a voice echoes, distorted, calling '{self.personality.name}'. do you respond?",
                "answer cautiously.",
                "remain silent.",
            ),
            (
                "your breath fogs up the glass as you press your forehead against it. do you wipe it away?",
                "wipe the condensation.",
                "let it fade on its own.",
            ),
            (
                "a familiar melody suddenly pulls you from the depths of sleep. you don’t remember falling asleep, yet here you are, waking to its soft tune. do you let it continue?",
                "hum along.",
                "turn it off.",
            ),
            (
                "as your senses return, you notice the blinking light on the control panel. it's faster now, as if trying to get your attention. do you press it?",
                "press it.",
                "do nothing.",
            ),
            (
                "you glance at the dashboard. an old photograph sits there, but as you reach for it, a shiver runs down your spine. do you pick it up?",
                "turn it over and look at the back.",
                "get up from your chair.",
            ),
            (
                "your reflection in the window doesn’t move when you do. it stares at you with empty eyes, its mouth slightly parted, as if whispering. do you look closer?",
                "lean in, heart pounding.",
                "look away, refusing to meet its gaze.",
            ),
            (
                "the ship hums around you, rhythmic and steady—until it isn’t. the sound distorts, twisting into something alive, something breathing. it isn’t just the ship. it’s something else. do you listen?",
                "match your breathing to it, feeling its presence.",
                "step back, pulse quickening.",
            ),
            (
                "the hum deepens, reverberating inside your chest. your vision swims. it’s calling you, a voice without words, a presence without form. something is waiting. something is watching. do you answer?",
                "whisper back, accepting the unknown.",
                "resist, holding onto yourself.",
            ),
            (
                "the air grows heavy. your reflection in the glass watches, waiting for your decision. if you answer, you will no longer be alone. if you resist, you may lose whatever is left of you. there is no turning back now.",
                "step forward, into the reflection.",
                "close your eyes, refusing to become what it wants.",
            )
        ]

        failure_texts = [
            "you ignore the lighthouse. the fog thickens, and soon, you can't see anything at all.",
            "you circle the lighthouse, but there is no other entrance. the wind howls around you.",
            "you take right, and fall into an infinite void. after all, maybe it is the only way to find peace.",
            "you wait too long, and the child vanishes before your eyes, leaving only silence.",
            "you hesitate to speak, and he turns away. a deep sadness settles in the room.",
            "you say nothing, and the child lowers his gaze. a long silence follows.",
            "you refuse to turn around, and the child fades away, leaving you alone once more.",
            "you wait instead of approaching, and the house dissolves like mist in the wind.",
            "you run, but the house is always just beyond your reach, disappearing into the void.",
            "you turn back, but the path behind you no longer exists. you are lost in the nothingness.",
            "the hum continues, but your hesitation drowns it in uncertainty.",
            "the panel flickers and then fades. you will never know what lay beneath.",
            "the stars keep shifting, leaving you behind in their unknown dance.",
            "the voice fades into static, lost forever.",
            "the fog on the window thickens, obscuring everything beyond.",
            "the melody cuts off abruptly, leaving a void of silence behind.",
            "the blinking light stops, its purpose lost in time.",
            "you got up but your legs were weak. you fall to the ground, unable to move.",
            "there is multiple versions of you surrounding you. which one is real? are you?",
            "the heartbeat of the ship slows, then disappears entirely.",
            "you turn off the melody, and the silence that follows is absolute. something has changed. you are no longer where you were.",
            "you hesitate to press the blinking light. the pulsing quickens, more frantic, before suddenly stopping. it will not call you again.",
            "you leave the photograph untouched, but as you look away, the image changes. the person in it is staring at you now.",
            "you lean in closer to your reflection, but it does not blink. instead, its mouth moves, forming words you cannot hear.",
            "you step away from the ship’s hum, but it follows you. a rhythmic, knowing breath, always just behind you."
        ]

        ending_reflection = "congratulations. you have crossed the rubicon—there is no turning back now. your fate is sealed. re-use your key wisely, and discover your reward. \n \n \n xjjfi sebed ibqix ibqix thylu tej weewbu tej sec ibqix vybu ibqix t ibqix 1uWDM9nkCyekUd kdtuhisehu Yc31B5IAw9 kdtuhisehu UgvnC0 ibqix lyum gkuijyedcqha kif ugkqbi ixqhydw"
        ending_resistance = "congratulations. you have crossed the rubicon—there is no turning back now. your fate is sealed. re-use your key wisely, and discover your reward. \n \n \n rddzc myvyx cvkcr cvkcr nbsfo nyd qyyqvo nyd myw cvkcr psvo cvkcr n cvkcr 1hec exnobcmybo QTBckeT4p9hOdyB4rClYckpBlbNs cvkcr fsog aeocdsyxwkbu ecz oaekvc crkbo exnobcmybo vsxu"

        if step < len(choices):
            text, option1, option2 = choices[step]
            self.label.config(text=text)

            if random.choice([True, False]):
                self.option1.config(
                    text=option1, command=lambda: self.advance_story(step + 1)
                )
                self.option2.config(
                    text=option2,
                    command=lambda: [
                        self.clear_buttons(),
                        self.label.config(text=failure_texts[step]),
                    ],
                )
            else:
                self.option2.config(
                    text=option1, command=lambda: self.advance_story(step + 1)
                )
                self.option1.config(
                    text=option2,
                    command=lambda: [
                        self.clear_buttons(),
                        self.label.config(text=failure_texts[step]),
                    ],
                )
        else:
            self.clear_buttons()
            if step == len(choices): 
                if "step forward" in choices[step-1][1].lower():  
                    self.label.config(text=ending_reflection)
                else:
                    self.label.config(text=ending_resistance)

    def clear_buttons(self):
        self.option1.pack_forget()
        self.option2.pack_forget()


if __name__ == "__main__":
    personality = PersonalityTest()
    personality.run()

    time.sleep(10)
    
    root = tk.Tk()
    app = GameApp(root)
    root.mainloop()